mapper.select:
select

from killrate_award
where game_id = #{gameId,jdbcType=INTEGER}

and issue_end_time > #{issueEndTime,jdbcType=TIMESTAMP}

order by issue_end_time

mapper.insert:
insert into killrate_award (id, game_id, issue,
award_number, award_time, issue_end_time, is_push, killrate, award_money, bet_money)
values (#{id,jdbcType=INTEGER}, #{gameId,jdbcType=VARCHAR}, #{issue,jdbcType=VARCHAR},
#{awardNumber,jdbcType=VARCHAR}, #{awardTime,jdbcType=TIMESTAMP},
#{issueEndTime,jdbcType=TIMESTAMP}, #{isPush,jdbcType=INTEGER}, #{killrate,jdbcType=INTEGER}
, #{award_money}, #{bet_money}
)

mapper.select:
    	select 

	    from killrate_award
	    where game_id = #{gameId,jdbcType=VARCHAR}

	    and issue_end_time > #{operateTime,jdbcType=TIMESTAMP} 


	    	and	issue = #{issue,jdbcType=VARCHAR}



	    	and	issue_end_time > #{startTime,jdbcType=TIMESTAMP} 
	    	and issue_end_time <= #{endTime,jdbcType=TIMESTAMP} 


	    order by issue_end_time

mapper.delete:
    	delete from killrate_award 
	    where game_id = #{gameId,jdbcType=VARCHAR}

	    and issue_end_time > #{operateTime,jdbcType=TIMESTAMP} 


	    	and	issue = #{issue,jdbcType=VARCHAR}



	    	and	issue_end_time > #{startTime,jdbcType=TIMESTAMP} 
	    	and issue_end_time <= #{endTime,jdbcType=TIMESTAMP}

mapper.select:
select a.full_issue as issue , b.game_id, b.is_push, b.award_number,b.award_money,b.bet_money,b.killrate from game_issue_${gameId} a
left join killrate_award b on a.game_id = b.game_id and a.full_issue = b.issue
where a.game_id = #{gameId}
and a.issue_date = #{issueDate,jdbcType=DATE}

and b.is_push = 1

	select 

	from killrate_award
    where game_id = #{gameId,jdbcType=VARCHAR}
	and issue = #{issue,jdbcType=VARCHAR}
